import React from "react";

const FrageKarte = ({ frage, antwort, nummer }) => (
    <div style={{
        border: "1px solid #ccc",
        borderRadius: "8px",
        padding: "10px",
        marginBottom: "12px"
    }}>
        <h3>Frage {nummer}</h3>
        <p>{frage}</p>
        <p style={{ color: "gray", marginTop: "10px" }}>{antwort}</p>
    </div>
);

export default FrageKarte;
